INSERT INTO `zx_article` VALUES (1,'18101518137047','虚伪，永远换不来真心','20181008\\5c7f7c929592dbfbf86cf23e65abc784.jpg','<p align=\"center\" style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 8px; font-size: 1.1em; color: rgb(136, 136, 136); letter-spacing: 0.6px; white-space: normal; font-family: \" microsoft=\"\"><span style=\"box-sizing: border-box; color: rgb(89, 89, 89); font-size: 16px;\">路遥知马力，</span></p><p align=\"center\" style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 8px; font-size: 1.1em; color: rgb(136, 136, 136); letter-spacing: 0.6px; white-space: normal; font-family: \" microsoft=\"\"><span style=\"box-sizing: border-box; color: rgb(89, 89, 89); font-size: 16px;\">日久见人心；</span></p><p align=\"center\" style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 8px; font-size: 1.1em; color: rgb(136, 136, 136); letter-spacing: 0.6px; white-space: normal; font-family: \" microsoft=\"\"><span style=\"box-sizing: border-box; color: rgb(89, 89, 89); font-size: 16px;\">时间是个好东西，</span></p><p align=\"center\" style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 8px; font-size: 1.1em; color: rgb(136, 136, 136); letter-spacing: 0.6px; white-space: normal; font-family: \" microsoft=\"\"><span style=\"box-sizing: border-box; color: rgb(89, 89, 89); font-size: 16px;\">见证了人性，</span></p><p align=\"center\" style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 8px; font-size: 1.1em; color: rgb(136, 136, 136); letter-spacing: 0.6px; white-space: normal; font-family: \" microsoft=\"\"><span style=\"box-sizing: border-box; color: rgb(89, 89, 89); font-size: 16px;\">也见证了一颗真心。</span></p><p align=\"center\" style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 8px; font-size: 1.1em; color: rgb(136, 136, 136); letter-spacing: 0.6px; white-space: normal; font-family: \" microsoft=\"\"><span style=\"box-sizing: border-box; color: rgb(89, 89, 89); font-size: 16px;\"><br style=\"box-sizing: border-box;\"/></span></p><p class=\"pgc-img-caption\" align=\"center\" style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 8px; font-size: 1.1em; color: rgb(136, 136, 136); letter-spacing: 0.6px; white-space: normal; font-family: \" microsoft=\"\"></p><p align=\"center\" style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 8px; font-size: 1.1em; color: rgb(136, 136, 136); letter-spacing: 0.6px; white-space: normal; font-family: \" microsoft=\"\"><span style=\"box-sizing: border-box; color: rgb(89, 89, 89); font-size: 16px;\">人与人的相处，</span></p><p align=\"center\" style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 8px; font-size: 1.1em; color: rgb(136, 136, 136); letter-spacing: 0.6px; white-space: normal; font-family: \" microsoft=\"\"><span style=\"box-sizing: border-box; color: rgb(89, 89, 89); font-size: 16px;\">像场修行的旅程，</span></p><p align=\"center\" style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 8px; font-size: 1.1em; color: rgb(136, 136, 136); letter-spacing: 0.6px; white-space: normal; font-family: \" microsoft=\"\"><span style=\"box-sizing: border-box; color: rgb(89, 89, 89); font-size: 16px;\">短期交往看脾气；</span></p><p align=\"center\" style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 8px; font-size: 1.1em; color: rgb(136, 136, 136); letter-spacing: 0.6px; white-space: normal; font-family: \" microsoft=\"\"><span style=\"box-sizing: border-box; color: rgb(89, 89, 89); font-size: 16px;\">长期交往看德行；</span></p><p align=\"center\" style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 8px; font-size: 1.1em; color: rgb(136, 136, 136); letter-spacing: 0.6px; white-space: normal; font-family: \" microsoft=\"\"><span style=\"box-sizing: border-box; color: rgb(89, 89, 89); font-size: 16px;\">一生交往看人品。</span></p><p align=\"center\" style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 8px; font-size: 1.1em; color: rgb(136, 136, 136); letter-spacing: 0.6px; white-space: normal; font-family: \" microsoft=\"\"><img src=\"https://www.myzxx.cn/static/umeditor/upload/20181008/01b7e83d.jpg\" _src=\"https://www.myzxx.cn/static/umeditor/upload/20181008/01b7e83d.jpg\" style=\"box-sizing: border-box; border-width: 0px; border-style: initial; max-width: 100%; margin-bottom: 8px;\"/></p><p class=\"pgc-img-caption\" align=\"center\" style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 8px; font-size: 1.1em; color: rgb(136, 136, 136); letter-spacing: 0.6px; white-space: normal; font-family: \" microsoft=\"\"></p><p align=\"center\" style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 8px; font-size: 1.1em; color: rgb(136, 136, 136); letter-spacing: 0.6px; white-space: normal; font-family: \" microsoft=\"\"><span style=\"box-sizing: border-box; color: rgb(89, 89, 89); font-size: 16px;\">脾气的好坏，</span></p><p align=\"center\" style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 8px; font-size: 1.1em; color: rgb(136, 136, 136); letter-spacing: 0.6px; white-space: normal; font-family: \" microsoft=\"\"><span style=\"box-sizing: border-box; color: rgb(89, 89, 89); font-size: 16px;\">决定开始的相处；</span></p><p align=\"center\" style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 8px; font-size: 1.1em; color: rgb(136, 136, 136); letter-spacing: 0.6px; white-space: normal; font-family: \" microsoft=\"\"><span style=\"box-sizing: border-box; color: rgb(89, 89, 89); font-size: 16px;\">德行的高低，</span></p><p align=\"center\" style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 8px; font-size: 1.1em; color: rgb(136, 136, 136); letter-spacing: 0.6px; white-space: normal; font-family: \" microsoft=\"\"><span style=\"box-sizing: border-box; color: rgb(89, 89, 89); font-size: 16px;\">决定交往的质量；</span></p><p align=\"center\" style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 8px; font-size: 1.1em; color: rgb(136, 136, 136); letter-spacing: 0.6px; white-space: normal; font-family: \" microsoft=\"\"><span style=\"box-sizing: border-box; color: rgb(89, 89, 89); font-size: 16px;\">人品的正邪，</span></p><p align=\"center\" style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 8px; font-size: 1.1em; color: rgb(136, 136, 136); letter-spacing: 0.6px; white-space: normal; font-family: \" microsoft=\"\"><span style=\"box-sizing: border-box; color: rgb(89, 89, 89); font-size: 16px;\">决定一辈子的情感。</span></p><p align=\"center\" style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 8px; font-size: 1.1em; color: rgb(136, 136, 136); letter-spacing: 0.6px; white-space: normal; font-family: \" microsoft=\"\"><span style=\"box-sizing: border-box; color: rgb(89, 89, 89); font-size: 16px;\"><br style=\"box-sizing: border-box;\"/></span></p><p class=\"pgc-img-caption\" align=\"center\" style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 8px; font-size: 1.1em; color: rgb(136, 136, 136); letter-spacing: 0.6px; white-space: normal; font-family: \" microsoft=\"\"></p><p align=\"center\" style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 8px; font-size: 1.1em; color: rgb(136, 136, 136); letter-spacing: 0.6px; white-space: normal; font-family: \" microsoft=\"\"><span style=\"box-sizing: border-box; color: rgb(89, 89, 89); font-size: 16px;\">虚伪的人，</span></p><p align=\"center\" style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 8px; font-size: 1.1em; color: rgb(136, 136, 136); letter-spacing: 0.6px; white-space: normal; font-family: \" microsoft=\"\"><span style=\"box-sizing: border-box; color: rgb(89, 89, 89); font-size: 16px;\">总爱口是心非，</span></p><p align=\"center\" style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 8px; font-size: 1.1em; color: rgb(136, 136, 136); letter-spacing: 0.6px; white-space: normal; font-family: \" microsoft=\"\"><span style=\"box-sizing: border-box; color: rgb(89, 89, 89); font-size: 16px;\">表面一套，</span></p><p align=\"center\" style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 8px; font-size: 1.1em; color: rgb(136, 136, 136); letter-spacing: 0.6px; white-space: normal; font-family: \" microsoft=\"\"><span style=\"box-sizing: border-box; color: rgb(89, 89, 89); font-size: 16px;\">背后却是另一套。</span></p><p align=\"center\" style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 8px; font-size: 1.1em; color: rgb(136, 136, 136); letter-spacing: 0.6px; white-space: normal; font-family: \" microsoft=\"\"><span style=\"box-sizing: border-box; color: rgb(89, 89, 89); font-size: 16px;\"><br style=\"box-sizing: border-box;\"/></span></p><p class=\"pgc-img-caption\" align=\"center\" style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 8px; font-size: 1.1em; color: rgb(136, 136, 136); letter-spacing: 0.6px; white-space: normal; font-family: \" microsoft=\"\"></p><p align=\"center\" style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 8px; font-size: 1.1em; color: rgb(136, 136, 136); letter-spacing: 0.6px; white-space: normal; font-family: \" microsoft=\"\"><span style=\"box-sizing: border-box; color: rgb(89, 89, 89); font-size: 16px;\">也许他们能得到，</span></p><p align=\"center\" style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 8px; font-size: 1.1em; color: rgb(136, 136, 136); letter-spacing: 0.6px; white-space: normal; font-family: \" microsoft=\"\"><span style=\"box-sizing: border-box; color: rgb(89, 89, 89); font-size: 16px;\">想要的东西；</span></p><p align=\"center\" style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 8px; font-size: 1.1em; color: rgb(136, 136, 136); letter-spacing: 0.6px; white-space: normal; font-family: \" microsoft=\"\"><span style=\"box-sizing: border-box; color: rgb(89, 89, 89); font-size: 16px;\">但请相信，</span></p><p align=\"center\" style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 8px; font-size: 1.1em; color: rgb(136, 136, 136); letter-spacing: 0.6px; white-space: normal; font-family: \" microsoft=\"\"><span style=\"box-sizing: border-box; color: rgb(89, 89, 89); font-size: 16px;\">一切都是暂时的；</span></p><p align=\"center\" style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 8px; font-size: 1.1em; color: rgb(136, 136, 136); letter-spacing: 0.6px; white-space: normal; font-family: \" microsoft=\"\"><span style=\"box-sizing: border-box; color: rgb(89, 89, 89); font-size: 16px;\">只有真心，</span></p><p align=\"center\" style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 8px; font-size: 1.1em; color: rgb(136, 136, 136); letter-spacing: 0.6px; white-space: normal; font-family: \" microsoft=\"\"><span style=\"box-sizing: border-box; color: rgb(89, 89, 89); font-size: 16px;\">经得起长期考验。</span></p><p align=\"center\" style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 8px; font-size: 1.1em; color: rgb(136, 136, 136); letter-spacing: 0.6px; white-space: normal; font-family: \" microsoft=\"\"><span style=\"box-sizing: border-box; color: rgb(89, 89, 89); font-size: 16px;\"><img src=\"https://www.myzxx.cn/static/umeditor/upload/20181008/3697a1be.jpg\" _src=\"https://www.myzxx.cn/static/umeditor/upload/20181008/3697a1be.jpg\" style=\"box-sizing: border-box; border-width: 0px; border-style: initial; max-width: 100%; margin-bottom: 8px;\"/></span></p><p class=\"pgc-img-caption\" align=\"center\" style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 8px; font-size: 1.1em; color: rgb(136, 136, 136); letter-spacing: 0.6px; white-space: normal; font-family: \" microsoft=\"\"></p><p align=\"center\" style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 8px; font-size: 1.1em; color: rgb(136, 136, 136); letter-spacing: 0.6px; white-space: normal; font-family: \" microsoft=\"\"><span style=\"box-sizing: border-box; color: rgb(89, 89, 89); font-size: 16px;\">真诚待人，</span></p><p align=\"center\" style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 8px; font-size: 1.1em; color: rgb(136, 136, 136); letter-spacing: 0.6px; white-space: normal; font-family: \" microsoft=\"\"><span style=\"box-sizing: border-box; color: rgb(89, 89, 89); font-size: 16px;\">用心做事；</span></p>